#!/bin/bash\n\n# Atualizar pacotes do sistema\nsudo apt update && sudo apt upgrade -y\n\n# Instalar dependências do sistema\nsudo apt install -y git curl build-essential\n\n# Instalar Node.js e npm\ncurl -fsSL https://deb.nodesource.com/setup_18.x | sudo -E bash -\nsudo apt-get install -y nodejs\n\n# Instalar MongoDB\nwget -qO - https://www.mongodb.org/static/pgp/server-5.0.asc | sudo apt-key add -\necho "deb [ arch=amd64,arm64 ] https://repo.mongodb.org/apt/ubuntu xenial/mongodb-org/5.0 multiverse" | sudo tee /etc/apt/sources.list.d/mongodb-org-5.0.list\nsudo apt update\nsudo apt install -y mongodb-org\n\n# Iniciar MongoDB\nsudo systemctl start mongod\nsudo systemctl enable mongod\n\n# Clonar o repositório do projeto\ngit clone https://github.com/foxgts/erp-web-system.git\ncd erp-web-system\n\n# Instalar dependências do backend\ncd backend\nnpm install\n\n# Instalar dependências do frontend\ncd ../frontend/app\nnpm install\n\n# Build e iniciar o frontend\nnpm run build\n\n# Iniciar servidor de desenvolvimento do frontend e backend\n# No terminal, você pode usar 'npm start' ou 'npm run dev' conforme sua configuração preparada\nnpm run start & (cd ../../backend && node index.js &)\n\necho "Configuração completa! O sistema ERP está rodando."
